    id: "myViewer",
    scene: new Scene(),
    renderers: new WebGLRenderer({ // Mandatory
         textureTranscoder: new KTX2TextureTranscoder({ // Optional
             transcoderPath: "./../dist/basis/" // <------ Path to BasisU transcoder module
         })
